import React from "react";
import { Container, Navbar, ToggleButton, ToggleButtonGroup} from "react-bootstrap";
import { BsFillSunFill, BsMoonStarsFill } from "react-icons/bs";
import '../style/Footer.css';
import '../configuration/themes'

export default class Footer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            theme: 'light',
            radioValue: 0,
            buttons: [
                { name: 'btn-light', variant: 'light', action: 'light', icon: <BsFillSunFill/>, value: 0 },
                { name: 'btn-dark', variant: 'dark',  action: 'dark', icon: <BsMoonStarsFill/>, value: 1 }
            ]
        };
      }

    render() {

        return (
            <>
                <Navbar bg={this.state.theme} fixed="bottom">
                    <Container className="footer">

                        <ToggleButtonGroup type="radio" name="radio">
                            {this.state.buttons.map((radio, idx) => (
                                <ToggleButton
                                key={idx}
                                id={`radio-${idx}`}
                                variant={idx % 2 ? 'outline-light' : 'outline-dark'}
                                value={radio.value}
                                checked={this.state.radioValue === radio.value}
                                onChange={(e) => this.setState({radioValue: e.currentTarget.value})}
                                onClick={() => this.setState({theme: btn.action})} 
                                >
                                {radio.name}
                                </ToggleButton>
                            ))}
                        </ToggleButtonGroup>


                        {/* <ToggleButtonGroup className="btn-group" aria-label="Theme setter" type="radio" name="radio">

                            {
                                this.state.buttons.map((btn, index) => (
                                <ToggleButton
                                key={index}
                                variant={btn.variant} value={btn.value} 
                                onClick={() => this.setState({theme: btn.action})}         
                                >
                                    {btn.icon}
                                </ToggleButton>
                                )) 
                            }

                        </ToggleButtonGroup> */}


                    </Container>
                </Navbar>
            </>
        );
    }
}
