import React from "react";
import { Container, Navbar, ButtonGroup, ToggleButton, ToggleButtonGroup} from "react-bootstrap";
import { BsFillSunFill, BsMoonStarsFill } from "react-icons/bs";
import '../style/Footer.css';
import '../configuration/themes'

export default class Footer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            checked: false,
            theme: 'light',
            buttons: [
                { name: 'Light', value: '1', variant: 'light', action: 'light', icon: <BsFillSunFill/> },
                { name: 'Dark', value: '2', variant: 'dark',  action: 'dark', icon: <BsMoonStarsFill/> }
            ]
        };
      }

    render() {

        return (
            <>
                <Navbar bg={this.state.theme} fixed="bottom">
                    <Container className="footer">
                        <ToggleButtonGroup className="btn-group" aria-label="Theme setter">

                            {
                               this.state.buttons.map((btn, index) => (
                                <ToggleButton
                                key={index}
                                type="checkbox" variant={btn.variant} value={btn.value} 
                                onClick={() => this.setState({theme: btn.action})} 

                                >
                                    {btn.icon}
                                </ToggleButton>
                               )) 
                            }

                        </ToggleButtonGroup>
                    </Container>
                </Navbar>
            </>
        );
    }
}
