import React from "react";
import { Container, Navbar, ButtonGroup, ToggleButton} from "react-bootstrap";
import { BsFillSunFill, BsMoonStarsFill } from "react-icons/bs";
import '../style/Footer.css';
import '../configuration/themes'

export default class Footer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            checked: false,
            theme: light
        };
      }
    render() {
        return (
            <>
                <Navbar bg="dark" variant="dark" fixed="bottom">
                    <Container className="footer">
                        <ButtonGroup className="btn-group" aria-label="Theme setter">
                            <ToggleButton
                            type="checkbox"
                            variant={this.checked}
                            checked={this.theme}
                            value="1"
                            onChange={(e) => this.setState({ checked: e.currentTarget.checked })}
                            >
                                <BsFillSunFill/>
                            </ToggleButton>
                            <ToggleButton
                            type="checkbox"
                            variant={this.theme}
                            checked={this.checked}
                            value="1"
                            onChange={(e) => this.setState({ checked: e.currentTarget.checked })}
                            >
                                <BsMoonStarsFill/>
                            </ToggleButton>
                        </ButtonGroup>
                    </Container>
                </Navbar>
            </>
        );
    }
}
