import React from "react";
import { Container, Navbar, ButtonGroup, ToggleButton} from "react-bootstrap";
import { BsFillSunFill, BsMoonStarsFill } from "react-icons/bs";
import '../style/Footer.css';
import '../configuration/themes'

export default class Footer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            checked: false,
            theme: 'light',
            buttons: [
                { name: 'Light', value: '1', variant: 'light', icon: <BsFillSunFill/> },
                { name: 'Dark', value: '2', variant: 'dark',  icon: <BsMoonStarsFill/> }
            ]
        };
      }
    render() {

        function changeTheme(color) {
            this.setState({theme: 'dark'});
        }

        return (
            <>
                <Navbar bg="dark" variant="dark" fixed="bottom">
                    <Container className="footer">
                        <ButtonGroup className="btn-group" aria-label="Theme setter">

                            {
                               this.state.buttons.map((btn, index) => (
                                <ToggleButton
                                key={index}
                                type="checkbox" variant={this.state.theme} checked={btn.checked} value={btn.value} 
                                // onChange={(e) => this.setState({ checked: e.currentTarget.checked, theme: this.state.theme === 'light' ? this.state.theme = 'dark' :  this.state.theme = 'light' })}
                                onChange={console.log('test')}
                                onClick={changeTheme}
                                >
                                    {btn.icon}
                                </ToggleButton>
                               )) 
                            }

                        </ButtonGroup>
                    </Container>
                </Navbar>
            </>
        );
    }
}
